import React, { useState, useEffect } from 'react';
import { Container, TextField, Button } from '@mui/material';
import MultiSelectDropdown from './MultiSelectDropdown';
import PreviewList from '../PreviewList';

function App() {
  const [totalAmount, setTotalAmount] = useState(500);
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [preview, setPreview] = useState([]);
  const [grandTotal, setGrandTotal] = useState(0);
  const [editUserId, setEditUserId] = useState(null);

  useEffect(() => {
    fetch('/users.json')
      .then(response => response.json())
      .then(data => setUsers(data));

    fetch('/items.json')
      .then(response => response.json())
      .then(data => setItems(data));
  }, []);

  const handleSave = () => {
    if (editUserId !== null) {
      const updatedPreview = preview.map(p => 
        p.user === editUserId ? 
          { user: editUserId, items: selectedItems.map(itemId => items.find(i => i.id === itemId)), total: selectedItems.reduce((sum, itemId) => sum + items.find(i => i.id === itemId).price, 0) } 
          : p
      );
      setPreview(updatedPreview);
      setEditUserId(null);
    } else {
      const newPreview = selectedUsers.map(user => {
        const userItems = selectedItems.map(item => items.find(i => i.id === item));
        const total = userItems.reduce((sum, item) => sum + item.price, 0);
        return { user, items: userItems, total };
      });
      setPreview([...preview, ...newPreview]);
    }

    const grandTotal = preview.reduce((sum, { total }) => sum + total, 0);
    setGrandTotal(grandTotal);
  };

  const handleEdit = (user) => {
    const userPreview = preview.find(p => p.user === user);
    setSelectedUsers([user]);
    setSelectedItems(userPreview.items.map(item => item.id));
    setEditUserId(user);
  };

  const handleDelete = (user) => {
    const newPreview = preview.filter(p => p.user !== user);
    setPreview(newPreview);
    const grandTotal = newPreview.reduce((sum, { total }) => sum + total, 0);
    setGrandTotal(grandTotal);
  };

  return (
    <Container>
      <h1>Cafeteria</h1>
      <TextField
        label="Total Amount"
        type="number"
        value={totalAmount}
        onChange={(e) => setTotalAmount(e.target.value)}
        fullWidth
        margin="normal"
      />
      <MultiSelectDropdown
        label="Select Users"
        options={users}
        selectedValues={selectedUsers}
        setSelectedValues={setSelectedUsers}
      />
      <MultiSelectDropdown
        label="Select Food Items"
        options={items}
        selectedValues={selectedItems}
        setSelectedValues={setSelectedItems}
      />
      <Button variant="contained" color="primary" onClick={handleSave}>
        {editUserId !== null ? 'Update' : 'Save'}
      </Button>
      <PreviewList
        preview={preview}
        users={users}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />
      <h3>Grand Total: {grandTotal}</h3>
    </Container>
  );
}

export default App;
