import React, { useState, useEffect } from 'react';
import { Container, TextField, Button } from '@mui/material';
import MultiSelectDropdown from './components/MultiSelectDropdown';
import PreviewList from './components/PreviewList';

function App() {
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [preview, setPreview] = useState([]);
  const [grandTotal, setGrandTotal] = useState(0);
  const [editUserId, setEditUserId] = useState(null);

  useEffect(() => {
    fetch('/users.json')
      .then(response => response.json())
      .then(data => setUsers(data));

    fetch('/items.json')
      .then(response => response.json())
      .then(data => setItems(data));
  }, []);

  const handleSave = () => {
    let newPreview = [...preview];

    selectedUsers.forEach(user => {
      const existingUserIndex = newPreview.findIndex(p => p.user === user);

      const newItems = selectedItems.map(item => items.find(i => i.id === item));

      if (existingUserIndex !== -1) {
        // User exists: Add new items to existing items without removing existing ones
        const existingItems = newPreview[existingUserIndex].items;

        // Combine existing and new items, ensuring no duplicates
        const combinedItems = [...existingItems];
        newItems.forEach(newItem => {
          if (!existingItems.some(item => item.id === newItem.id)) {
            combinedItems.push(newItem);
          }
        });

        const updatedTotal = combinedItems.reduce((sum, item) => sum + item.price, 0);

        newPreview[existingUserIndex] = {
          ...newPreview[existingUserIndex],
          items: combinedItems,
          total: updatedTotal,
        };
      } else {
        // User does not exist: Add as new entry
        const total = newItems.reduce((sum, item) => sum + item.price, 0);
        newPreview.push({ user, items: newItems, total });
      }
    });

    setPreview(newPreview);
    setSelectedUsers([]);
    setSelectedItems([]);

    // Update the grand total
    const grandTotal = newPreview.reduce((sum, { total }) => sum + total, 0);
    setGrandTotal(grandTotal);
  };

