        p.user === editUserId ? 
          { user: editUserId, items: selectedItems.map(itemId => items.find(i => i.id === itemId)), total: selectedItems.reduce((sum, itemId) => sum + items.find(i => i.id === itemId).price, 0) } 
          : p
      );
      setPreview(updatedPreview);
      setEditUserId(null);
    } else {
      for (const user of selectedUsers) {
        if (preview.find(p => p.user === user)) {
          alert(`User ${users.find(u => u.id === user).name} is already in the list. Please edit or delete the user.`);
          return;
        }
      }
      setPreview([...preview, ...newPreview]);
    }

    setSelectedUsers([]);
