import React, { useState, useEffect } from 'react';
import { Container, TextField, MenuItem, Button, List, ListItem, ListItemText } from '@mui/material';

function App() {
  const [totalAmount, setTotalAmount] = useState(500);
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [preview, setPreview] = useState([]);

  useEffect(() => {
    fetch('/users.json')
      .then(response => response.json())
      .then(data => setUsers(data));

    fetch('/items.json')
      .then(response => response.json())
      .then(data => setItems(data));
  }, []);

  const handleSave = () => {
    const newPreview = selectedUsers.map(user => {
      const userItems = selectedItems.map(item => items.find(i => i.id === item));
      const total = userItems.reduce((sum, item) => sum + item.price, 0);
      return { user, items: userItems, total };
    });
    setPreview(newPreview);
  };

  return (
    <Container>
      <h1>Cafeteria</h1>
      <TextField
        label="Total Amount"
        type="number"
        value={totalAmount}
        onChange={(e) => setTotalAmount(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        select
        label="Select Users"
        value={selectedUsers}
        onChange={(e) => setSelectedUsers(e.target.value)}
        SelectProps={{
          multiple: true,
        }}
        fullWidth
        margin="normal"
      >
        {users.map(user => (
          <MenuItem key={user.id} value={user.id}>
            {user.name}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Select Food Items"
        value={selectedItems}
        onChange={(e) => setSelectedItems(e.target.value)}
        SelectProps={{
          multiple: true,
        }}
        fullWidth
        margin="normal"
      >
        {items.map(item => (
          <MenuItem key={item.id} value={item.id}>
            {item.name}
          </MenuItem>
        ))}
      </TextField>
      <Button variant="contained" color="primary" onClick={handleSave}>
        Save
      </Button>
      <div>
        <h2>Preview</h2>
        <List>
          {preview.map(({ user, items, total }, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={`User: ${users.find(u => u.id === user).name}`}
                secondary={`Items: ${items.map(item => `${item.name} (${item.price})`).join(', ')} - Total: ${total}`}
              />
            </ListItem>
          ))}
        </List>
      </div>
    </Container>
  );
}

export default App;
