import React, { useState, useEffect } from 'react';
import { Container, TextField, Button } from '@mui/material';
import MultiSelectDropdown from './components/MultiSelectDropdown';
import PreviewList from './components/PreviewList';

function App() {
  const [totalAmount, setTotalAmount] = useState(500);
  const [users, setUsers] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [preview, setPreview] = useState([]);
  const [grandTotal, setGrandTotal] = useState(0);

  useEffect(() => {
    fetch('/users.json')
      .then(response => response.json())
      .then(data => setUsers(data));

    fetch('/items.json')
      .then(response => response.json())
      .then(data => setItems(data));
  }, []);

  const handleSave = () => {
    let newPreview = [...preview];

    selectedUsers.forEach(user => {
      const existingUserIndex = newPreview.findIndex(p => p.user === user);

      const newItems = selectedItems.map(item => items.find(i => i.id === item));

      if (existingUserIndex !== -1) {
        // Update user's items to exactly match the current selection
        const updatedTotal = newItems.reduce((sum, item) => sum + item.price, 0);

        newPreview[existingUserIndex] = {
          ...newPreview[existingUserIndex],
          items: newItems,
          total: updatedTotal,
        };
      } else {
        // Add new user entry
        const total = newItems.reduce((sum, item) => sum + item.price, 0);
        newPreview.push({ user, items: newItems, total });
      }
    });

    setPreview(newPreview);
    setSelectedUsers([]);
    setSelectedItems([]);

    const grandTotal = newPreview.reduce((sum, { total }) => sum + total, 0);
    setGrandTotal(grandTotal);
  };

  const handleEdit = (user) => {
    const userPreview = preview.find(p => p.user === user);

    if (userPreview) {
      setSelectedUsers([user]);
      setSelectedItems(userPreview.items.map(item => item.id));
    }
  };

  const handleDelete = (user) => {
    const newPreview = preview.filter(p => p.user !== user);
    setPreview(newPreview);
    const grandTotal = newPreview.reduce((sum, { total }) => sum + total, 0);
    setGrandTotal(grandTotal);
  };

  return (
    <Container>
      <h1>Cafeteria</h1>
      <TextField
        label="Total Amount"
        type="number"
        value={totalAmount}
        onChange={(e) => setTotalAmount(e.target.value)}
        fullWidth
        margin="normal"
      />
      <MultiSelectDropdown
        label="Select Users"
        options={users}
        selectedValues={selectedUsers}
        setSelectedValues={setSelectedUsers}
      />
      <MultiSelectDropdown
        label="Select Food Items"
        options={items}
        selectedValues={selectedItems}
        setSelectedValues={setSelectedItems}
      />
      <Button variant="contained" color="primary" onClick={handleSave}>
        Save
      </Button>
      <PreviewList
        preview={preview}
        users={users}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />
      <h3>Grand Total: {grandTotal}</h3>
    </Container>
  );
}

export default App;
