import React from 'react';
import { FormControl, InputLabel, Select, MenuItem, OutlinedInput, Chip, Box } from '@mui/material';

const MultiSelectDropdown = ({ label, options, selectedValues, setSelectedValues }) => {
  const handleChange = (event) => {
    alert(selectedValues+"mmm "+setSelectedValues)

    setSelectedValues(event.target.value);
  };

  return (
    <FormControl fullWidth margin="normal">
      <InputLabel>{label}</InputLabel>
      <Select
        multiple
        value={selectedValues}
        onChange={handleChange}
        input={<OutlinedInput label={label} />}
        renderValue={(selected) => (
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
            {selected.map((value) => (
              <Chip key={value} label={options.find(option => option.id === value).name} />
            ))}
          </Box>
        )}
      >
        {options.map(option => (
          <MenuItem key={option.id} value={option.id}>
            {option.name} {option.price}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
};

export default MultiSelectDropdown;
